    //MÃ¨tode cridat per cada registre
    public void run(DBObject obj){
        def foundEmails = []
        def words = obj.get('html').replaceAll('[^\\w@\\.-]',' ')
        for (w in words.tokenize(' ')){
            if(ptr.matcher(w).matches()){
                foundEmails << w.toLowerCase()
            }
        }
        obj.put("p_emails",foundEmails.unique())
    }
